import { Component, OnInit } from "@angular/core";
import { ComidaItem } from "src/app/shared/models/comida-item.model";
import { ServiceService } from "src/app/shared/service/service.service";

@Component({
  selector: 'app-comidas',
  templateUrl: './comidas.component.html',
  styleUrls: ['./comidas.component.css']
})
export class ComidasComponent implements OnInit {
  comidaLista: ComidaItem[] = [];
  mensagem = "Não foi encontrado registros";

  constructor(private comidaService: ServiceService) {}

  ngOnInit(): void {
    this.mensagem = "Carregando...";
    this.comidaService.getComidas().subscribe((result) => {
      console.log("result", result);
      this.comidaLista = result;
    });
  }
}
<div class="container my-5">
  <div
    class="row d-flex"
    *ngIf="comidaLista && comidaLista.length > 0; else naoTemRegistro"
  >
    <ng-container *ngFor="let item of comidaLista">
      <div class="col-12 col-md-6 col-lg-4 col-xl-3">
        <div class="card mb-3 col p-0">
          <img
            [src]="item.urlImagem"
            class="card-img-top"
            alt="Imagem do produto"
          />
          <div class="card-body">
            <h5 class="card-title">{{ item.titulo }}</h5>
            <p class="card-text">R$ {{ item.valor }}</p>
            <p class="card-text">
              {{ item.descricao }}
            </p>
            <a href="#" class="btn btn-primary">Adicionar</a>
          </div>
        </div>
      </div>
    </ng-container>
  </div>
</div>

<ng-template #naoTemRegistro>
  <div class="container my-5">
    <div class="alert alert-primary" role="alert">
      {{ mensagem }}
      <div class="spinner-border spinner-border-sm" role="status">
        <span class="visually-hidden">Loading...</span>
      </div>
    </div>
  </div>
</ng-template>
